import React, {useState} from 'react'
import './Login.css'
import { Link, useHistory } from "react-router-dom"; 
import axios from 'axios';
 

function Login() {
    const history = useHistory();
    // dont have null have empty string 
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const signIn = e => {
        // dont want refresing 
        e.preventDefault();
        // sign In code

        console.log(email, password)
         // Simple GET request using axios
        
        axios.get('/', {
            params: {
              email,
              password
            }
          }).then(response => console.log(`response = ${response}`))
        
    }

    const register = e => {
        e.preventDefault(); 
        // register code
    }


    return (
        <div className='login'>

            <Link to="/">
                <img 
                    className="login__logo"
                    src='data:image/png;base64,'></img>
            </Link>

            <div className="login__container">
                <h1>Sign-in</h1>

                <form>
                    <h5>Email</h5>
                    {/* Set value to of input field to what is being set in email state, as it is being entered */}
                    <input type='text' value={email} 
                        onChange={ e => setEmail(e.target.value)}/>

                    <h5>Password</h5>
                    {/* obscures pw charcters with type pw */}
                    <input type='password' value={password} 
                        onChange={ e => setPassword(e.target.value)} />

                    <button 
                        onClick={signIn} 
                        type='submit'
                        className='login__signInButton'
                        >Sign-in
                    </button>
                </form>

                <p> 
                    By signing-in you agree to the AMAZON CLONE Condition of Use & Sale. Please see our non-exising privacy notice, cookies notices, and other stuff. 
                </p>
                 
                 <button 
                    onClick={register}
                    type='submit'
                    className="login__registerButton">Create your Amazon Account
                </button>
            </div>
  
        </div>
    )
}

export default Login
